return {	--[[ How To Play ]]	{		-- id		"how_to_play",		-- trigger		function(self) return love.keyboard.isDown("f1") and self.active end,		-- on trigger		function(self)			text:show_text("How To Play:\n\n     Regular Mode   \n[WASD]      Move\n[SPACE]    Jump\n[ENTER]    Toggle Edit Mode\n\n     Edit Mode \n[WHEEL]    Select Letter\n[LMB]         Place, Remove Letter")		end,		-- update		function(self, dt)			if not love.keyboard.isDown("f1") then self.active = true end		end	},	--[[ First Chest ]]--	{		-- id		"first_chest",		-- trigger		function(self) return player_in_box(544, 208, 16, 16) end,		-- on trigger		function(self)			local letters = inventory:give_balanced_letters(16)			text:show_text("You found some letters!\n\n(" .. letters:upper() .. ")")			events:remove_event(self.id)			map.tiles[14][35].type = "chest_open"		end	},	--[[ Faraway Chest ]]--	{		-- id		"faraway_chest",		--trigger		function(self) return player_in_box(69*16, 16*16, 16, 16) end,		-- on trigger		function(self)			local letters = inventory:give_balanced_letters(12)			map.tiles[17][70].type = "chest_open"			text:show_text("There's some more letters in this box...\n\n(" .. letters:upper() .. ")")			events:remove_event(self.id)		end	},	--[[ No House ]]--	{		-- id		"no_house",		-- trigger		function(self) return player_in_box(107*16, 24*16, 16, 16) end,		-- on trigger		function(self)			map.tiles[25][108].type = "chest_open"			local letters = inventory:give_balanced_letters(18)			text:show_text("Safety Warning: The Spikes are Deadly!\nDO NOT TOUCH!")			text:show_text("You take some letters from the box.\n\n(" .. letters:upper() .. ")")			events:remove_event(self.id)		end	},	--[[ Almost There ]]	{		-- id		"almost_there",		-- trigger		function(self) return player_in_box(135*16, 24*16, 16, 16) end,		-- on trigger		function(self)			map.tiles[25][136].type = "chest_open"			local letters = inventory:give_balanced_letters(24)			text:show_text("Almost there!")			text:show_text("You grab a pile of letters.\n\n(" .. letters:upper() .. ")")			events:remove_event(self.id)		end	},	--[[ Death ]]	{	-- id		"death",		-- trigger		function(self) return player.dead and player.death_timer >= 1 and not text.shown end,		-- on trigger		function(self)			player:reset()		end	},	--[[ Wizard ]]	{		--id		"wizard",		-- trigger		function(self) return self.active and player_in_box(24*16, 9*16, 16, 6*16) end,		-- on trigger		function(self)			self.active = false			if player.coins >= 3 then				text:show_text("Very well! I will trade three of your star coins for rare letter sets!", function()					player.coins = player.coins - 3				end)				text:show_text("...")				text:show_text("... Abrakadabra...")				text:show_text("...")								local sets = {					{name = "vowel set",						desc = "It contains all 5 vowels!", letters = {							a = 1,							e = 1,							i = 1,							o = 1,							u = 1						}					},					{name = "magic set",						desc = "It contains the most MAGICal letters of the alphabet!", letters = {							m = 2,							a = 1,							g = 2,							i = 1,							c = 2						}					},					{name = "plosive set",						desc = "It contains the most powerful letters of the alphabet!", letters = {							p = 2,							t = 2,							k = 2,							b = 2,							d = 2,							g = 2						}					}				}				local set = sets[math.random(1, #sets)]								for k, v in pairs(set.letters) do					inventory:add_letter(k, v)				end								text:show_text("TADA!!!!!!!!!!!!!!!!!!!!")				text:show_text("You get the " .. set.name .. "!")				text:show_text(set.desc)							else				text:show_text("I am the almighty Wizard!\n\nI am looking for star coins. Come back to me if you want to trade 3 of them for a few rare letter sets!")			end		end,		-- update		function(self, dt)			if not player_in_box(24*16, 9*16, 16, 6*16) then				self.active = true			end		end	},	--[[ Ending ]]--	{		-- id		"ending",		-- trigger		function(self) return player_in_box(146*16, 16*16, 16, 16) end,		-- on trigger		function(self)			text:show_text("You made it!\nYou've reached the end of the game!")			end_reached = 1			music.ending:play()			if player.coins < 9 then				text:show_text("But although you feel good with yourself, something doesn't sit quite right...")				text:show_text("That's it! The 9 star coins!\n\nDid you forget to collect all of them or did you give them away?")				text:show_text("Maybe you should try again and grab all nine?")				text:show_text("But anyway... Thank you for playing this game!", function() show_end_screen = true end)			else				text:show_text("And you even managed to collect all nine star coins! Congratulations!")				text:show_text("Since you've gone this far, I hope you've liked the time you spent playing my game!")				text:show_text("Thank you for playing my game!", function() show_end_screen = true end)			end			events:remove_event(self.id)		end	}}